// Validate required fields
const data = $input.first().json.body;

const required = ['interaction_id', 'telegram_user_id', 'feedback_text', 'feedback_type'];
const missing = required.filter(field => !data[field]);

if (missing.length > 0) {
  throw new Error(`Missing required fields: ${missing.join(', ')}`);
}

return {
  interaction_id: data.interaction_id,
  telegram_user_id: data.telegram_user_id,
  feedback_text: data.feedback_text,
  feedback_type: data.feedback_type,
  context: data.context || {}
};

// Generate story ID: FEEDBACK-{type}-{timestamp}
const input = $input.first().json;
const type = input.feedback_type.toUpperCase().replace('_', '-');
const timestamp = new Date().toISOString().replace(/[-:]/g, '').slice(0, 15);
const story_id = `FEEDBACK-${type}-${timestamp}`;

return {
  ...input,
  story_id,
  project_id: 1
};

// Build prompt for Claude
const data = $input.first().json;
const ctx = data.context;

const prompt = `# USER FEEDBACK - FIX PROPOSAL

## Issue Report
Feedback: "${data.feedback_text}"
Type: ${data.feedback_type}

## Context
Equipment: ${ctx.equipment_number || 'Unknown'}
Manufacturer: ${ctx.manufacturer || 'Unknown'}
Model: ${ctx.model || 'Unknown'}
Manual URL: ${ctx.manual_url || 'N/A'}

## Task
1. Analyze the issue based on the feedback
2. Propose a specific fix that addresses the root cause
3. Identify all files that need to be changed
4. Write clear acceptance criteria for testing

## Response Format (JSON only, no markdown):
{
  "analysis": "What went wrong and why",
  "proposed_fix": "Specific solution with implementation details",
  "files_to_change": ["path/to/file1.py", "path/to/file2.py"],
  "estimated_effort": "low|medium|high",
  "acceptance_criteria": [
    "Criterion 1: Expected behavior after fix",
    "Criterion 2: How to verify the fix works"
  ],
  "priority": 2
}

Be specific and actionable. The fix will be implemented automatically by an AI agent.`;

return {
  ...data,
  prompt_text: prompt
};

// Parse Claude response
const data = $input.first().json;
const prev = $('Build Claude Prompt').first().json;

let proposal;
try {
  const content = data.content[0].text;

  // Try to extract JSON from markdown code blocks
  const jsonMatch = content.match(/```(?:json)?\s*([\s\S]*?)```/) || [null, content];
  proposal = JSON.parse(jsonMatch[1].trim());

} catch (e) {
  // Fallback if parsing fails
  proposal = {
    analysis: "Failed to parse Claude response",
    proposed_fix: data.content[0].text,
    files_to_change: [],
    estimated_effort: "medium",
    acceptance_criteria: ["Manual review required"],
    priority: 2
  };
}

return {
  ...prev,
  proposal,
  raw_claude_response: data.content[0].text
};

// Format Telegram message with proposal
