const p = data.proposal;

const effort_emoji = {
  low: 'üü¢',
  medium: 'üü°',
  high: 'üî¥'
}[p.estimated_effort] || '‚ö™';

const message = `üîß <b>Fix Proposal</b> (${data.story_id})

<b>Issue:</b>
${p.analysis}

<b>Proposed Solution:</b>
${p.proposed_fix}

<b>Files to Change:</b>
${p.files_to_change.map(f => `‚Ä¢ <code>${f}</code>`).join('\n') || 'None specified'}

<b>Effort:</b> ${effort_emoji} ${p.estimated_effort}

<b>Acceptance Criteria:</b>
${p.acceptance_criteria.map((c, i) => `${i+1}. ${c}`).join('\n')}

<i>Review the proposal and approve to begin implementation.</i>`;

return {
  ...data,
  message_text: message
};
