// Map through all incoming items
 return $input.all().map(item => {
   const data = item.json;

   // Safely extract URL and Context from the body or root
   const url = data.body?.url || data.url || '';
   const context = data.body?.context || data.context || {};

   // Validation logic (simpler, more reliable)
   let validFormat = false;
   let error = null;

   if (!url || url.trim() === '') {
     error = 'URL is required';
   } else if (!url.startsWith('http://') && !url.startsWith('https://')) {
     error = 'URL must start with http:// or https://';
   } else {
     // URL has valid protocol - mark as valid
     validFormat = true;
   }

   // Return in the standard n8n format
   return {
     json: {
       url,
       context,
       validFormat,
       error
     }
   };
 });

const headResponse = $input.item.json;

// Extract what we need directly from headers
let statusCode = headResponse.statusCode || 0;
let contentType = '';
let contentLength = 0;
let validContentType = false;
let error = null;

if (statusCode >= 200 && statusCode < 300) {
  // Extract headers
  const headers = headResponse.headers || {};
  contentType = headers['content-type'] || headers['Content-Type'] || '';
  contentLength = parseInt(headers['content-length'] || headers['Content-Length'] || '0', 10);

  // Check if content type is valid for a manual
  const lowerContentType = contentType.toLowerCase();
  validContentType = lowerContentType.includes('pdf') || 
                     lowerContentType.includes('html') || 
                     lowerContentType.includes('text');

  if (!validContentType) {
    error = 'Content type ' + contentType + ' not suitable for manuals';
  }
} else {
  error = 'HTTP ' + statusCode + ': Failed to access URL';
}

return {
  json: {
    statusCode: statusCode,
    contentType: contentType,
    contentLength: contentLength,
    validContentType: validContentType,
    error: error
  }
};

const data = $input.item.json;

// Calculate validation score (0-10)
let score = 0;
let valid = false;
let warnings = [];

if (!data.error) {
  valid = true;

  // Score based on HTTP status (0-3 points)
  if (data.statusCode >= 200 && data.statusCode < 300) {
    score += 3;
  } else if (data.statusCode >= 300 && data.statusCode < 400) {
    score += 2;
    warnings.push('HTTP ' + data.statusCode + ': URL redirects');
  }

  // Score based on content type (0-4 points)
  if (data.contentType.toLowerCase().includes('pdf')) {
    score += 4;
  } else if (data.contentType.toLowerCase().includes('html')) {
    score += 3;
    warnings.push('HTML format may require additional parsing');
  } else if (data.contentType.toLowerCase().includes('text')) {
    score += 2;
    warnings.push('Plain text format detected');
  }

  // Score based on file size (0-3 points)
  const sizeMB = data.contentLength / (1024 * 1024);
  if (sizeMB > 0.1 && sizeMB < 50) {
    score += 3;
  } else if (sizeMB >= 50 && sizeMB < 100) {
    score += 2;
    warnings.push('Large file size: ' + sizeMB.toFixed(1) + 'MB');
  } else if (sizeMB >= 100) {
    score += 1;
    warnings.push('Very large file size: ' + sizeMB.toFixed(1) + 'MB');
  } else if (sizeMB > 0) {
    score += 2;
    warnings.push('Small file size: ' + (sizeMB * 1024).toFixed(1) + 'KB');
  }
}

return {
  json: {
    valid: valid,
    status_code: data.statusCode,
    content_type: data.contentType,
    file_size_bytes: data.contentLength,
    score: score,
    warnings: warnings,
    error: data.error
  }
};

const data = $input.item.json;

const response = {
  valid: data.valid,
