const data = $input.item.json;

// Build the standardized error response
const response = {
  valid: false,
  status_code: 0,
  content_type: '',
  file_size_bytes: 0,
  score: 0,
  warnings: [],
  error: data.error || 'Invalid URL format'
};

// In "Each Item" mode, you return the object directly
return { json: response };

